import { ADD_FILTER, REMOVE_FILTER, CLEAR_FILTER } from "./filter-action";

export const filterReduser = (state = [], action) => {
  switch (action.type) {
    case CLEAR_FILTER: {
      return [];
    }

    case ADD_FILTER: {
      if (!state.includes(action.filer)) {
        return [...state, action.filter];
      }
      return state;
    }

    case REMOVE_FILTER: {
      return state.filter((i) => i !== action.filer);
    }

    default: {
      return state;
    }
  }
};
